INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','114','fdc63011e24cdf153a0e47f61a08236f.png','ALPINA','ALPINA','德系','德国','Alpina是一家位于德国巴伐利亚州的汽车生产公司，通常被认为是针对宝马车型的改装厂，但是Alpina实际上作为汽车生产商通过了T"UV认证，因此Alpina也被看做是德国最小的汽车生产商之一。','A','2023-12-18 04:44:56','2023-12-18 04:44:56');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','211','8e74f72b880c9b03287641709354e1b5.png','ABT','ABT','德系','德国','ABT Sportsline为一家族企业，ABT就是这个家族的姓氏，1896年创立于南德巴伐利亚邦的坎普顿(Kempten)。ABT的历史可以追溯到1896年由一家制造马车零件的小铁工厂起家，随着汽车工业的蓬勃发展，以及赛车的兴起，这家工厂的规模也日益扩大。','A','2023-12-18 04:44:57','2023-12-18 04:44:57');
