INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','281','180907595c15cc3a2667e9c93e7abb7a.png','Apollo','Apollo','德系','德国','阿波罗跑车是德国奥迪汽车公司生产的一款运动跑车，该车由贡佩尔特负责设计，每年制造销售50辆至60辆“阿波罗”运动跑车。“阿波罗”的基本型售价为22.968万欧元(约合27.2万美元)。
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','177','b02459207fdc825e9e09c7c958ac929b.png','Arash','Arash','其它','英国','Arash Motor Company是一家英国的超跑制造商，它最初由Arash Farboud于1999年成立，当时名为Farboud Limited，并于2006年更名。Arash首款产品AF-10新车的售价高达35.6万欧元，竞争对手包括了Koenigsegg和Pagani等品牌的超级跑车。AF-10将在未来五年内生产70辆。','A','2023-12-18 04:44:59','2023-12-18 04:44:59');
