INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','312','1159ab3d3c3e61c9c3a93abe4c5c8856.png','Aurus','Aurus','欧系','俄罗斯','Aurus Motors是俄罗斯豪华汽车品牌。Aurus其实是俄罗斯和金色的集合体，加在一起就是金色的俄罗斯的意思。Aurus第一次出现是在2018年5月的普京就职典礼上乘坐的就是Aurus Senat。','A','2023-12-18 04:45:00','2023-12-18 04:45:00');
