INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','3','846cc8f372b99c3e43c58eab5d43ce15.png','奔驰','Mercedes-Benz','德系','德国','梅赛德斯-奔驰（德语：Mercedes-Benz，根据地区与时期的不同分别有奔驰、宾士、平治、梅赛德斯、朋驰等多种中文简称），是一家以豪华和高性能著称的德国汽车品牌，总部设于德国斯图加特。
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','4','8cee3523929676aedd6f50c11ba3bf61.png','宝马','Bavarian Motor Work','德系','德国','宝马也被译为（巴依尔），是指宝马汽车公司(Bayerische Motoren Werke AG，简称BMW)生产的汽车，正式全称为巴伐利亚机械制造厂股份公司，是驰名世界的汽车企业，也被认为是高档汽车生产业的先导，主要的系列车型有1、2、3、4、5、6、7、8、i、X、Z等系列。','B','2023-12-18 04:45:11','2023-12-18 04:45:11');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','9','73279609532ac38418d96fe67cb82b17.png','本田','Honda','日系','日本','本田公司是世界上最大的摩托车生产厂家，汽车产量和规模也名列世界十大汽车厂家之列。本田于1948年以生产自行车助力发动机起步的Honda，一直以“梦想”作为原动力，以“商品”的形式不断为个人和社会提供更广泛的移动文化。“尊重个性”、重视每一个人个性的观念，使Honda形成了推崇员工创造性、自由豁达的企业文化。现在，Honda已经发展成为从小型通用发动机、踏板摩托车乃至跑车等各个领域都拥有独创技术，并不断研发、生产新产品的企业。','B','2023-12-18 04:45:14','2023-12-18 04:45:14');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','12','5f2960acf2a328bc49fd28a76939b41a.png','别克','BUICK','美系','美国','别克的商标图案是以一个圆圈中包含三个盾为基本图案。它的排列给人们一种起点高并不断攀登的感觉，象征着一种积极进取，不断登攀的精神。它的由来可以直接追溯到汽车制造业的奠基人－苏格兰人大卫-邓巴-别克的家徽。','B','2023-12-18 04:45:19','2023-12-18 04:45:19');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','16','c1df382871df315d681f4700821baac9.png','比亚迪','BYD','自主','中国','比亚迪股份有限公司（简称比亚迪）是一家在香港上市的中国民营企业，总部设于广东深圳，主要从事二次充电电池业务、手机部件及组装业务，以新能源汽车业务，同时积极拓展新能源产品领域的相关业务。','B','2023-12-18 04:45:22','2023-12-18 04:45:22');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','20','4acb95884373bbb907c61da96746a410.png','保时捷','porsche','德系','德国','保时捷(Porsche)是世界著名的高端汽车企业，以开发、生产和营销豪华跑车和运动型越野车为主，其总部位于德国斯图加特市，由费迪南德・保时捷(Ferdinand Porsche)创办。保时捷一直努力将种种可能性与看似不太可能的东西相组合，而今对于跑车而言，“保时捷”无异于一个全球意义上的代名词。为了践履“造型完美时，性能就会伴随而至”的“纯种跑车”造车工艺精神，费迪南德・保时捷于五十五岁时离开了福斯，自立门户，却因此而造就了跑车的不朽巨人――保时捷。','B','2023-12-18 04:45:39','2023-12-18 04:45:39');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','27','244c20d48e70e7abeeb57fcb2377404c.png','奔腾','BESTURN','自主','中国','奔腾品牌创立于2006年，将中国自主乘用车事业拓展到一个新的高度，并由此开启了一汽奔腾自主发展的崭新篇章。','B','2023-12-18 04:45:40','2023-12-18 04:45:40');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','47','5280933c321d155d4fbe8a95f71c14fc.png','宾利','Bentley','英系','英国','宾利（BentleyMotors Limited），英国豪华汽车品牌，总部位于英国克鲁。1919年，W.O.宾利先生创办了宾利汽车公司。在随后的几十年中，宾利陆续推出了S系列、T系列、Corniche、Camargue、Mulsanne慕尚等几款车型。','B','2023-12-18 04:45:41','2023-12-18 04:45:41');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','366','0bf00f22d11156537a8baed4eb787a87.png','宝骏','bao jun','自主','中国','宝骏是上汽通用五菱2010年创建的自主汽车品牌，宝骏品牌源由“骏”的本义是良驹，宝骏即人们最心爱的良驹。宝骏汽车充分集成了上汽、通用、五菱三方优势资源，品牌定位为“可靠的伙伴”，以“乐观进取、稳健可靠、精明自信”为品牌精神，旨在为消费者提供“具有国际标准和高可靠性，拥有成本和使用成本低，使客户拥有价值最大化，以超越顾客期望”的汽车品牌。','B','2023-12-18 04:45:42','2023-12-18 04:45:42');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','61','feae964d5447d24afccd3eff9b5c222f.png','标致','PEUGEOT','法系','法国','标致是法国、意大利及美国合资的Stellantis集团（Stellantis N.V.）旗下子品牌，又称宝{或普狮，是一间历史久远且对汽车工业的发展有重大贡献的汽车厂。','B','2023-12-18 04:45:43','2023-12-18 04:45:43');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','52','3dd96d7420bca289146585c502720630.png','北京','BAIC','自主','中国','北京汽车集团越野车有限公司（以下简称北汽越野车公司）是北汽集团军车越野车业务的重要板块。近年来，北汽越野车公司肩扛“越野世家”大旗，担当产业报国使命，致力于自主品牌军用轻型越野车和民用越野车的研发和生产制造，取得了一系列新发展成绩，北京80和北京40系列产品深受军方和消费者的青睐。','B','2023-12-18 04:45:46','2023-12-18 04:45:46');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','68','3e8f1ff7564e05e30b354a39c639f815.png','北京汽车','BAIC','自主','中国','北京汽车品牌是北汽集团整合旗下北汽新能源和北京汽车的产品与技术资源全力打造的核心品牌，代表着北汽“高、新、特”战略中的“新”字主力军，将以新能源、新技术为核心，推动北汽自主乘用车业务全面创新发展，开启北汽集团自主发展新篇章。','B','2023-12-18 04:45:46','2023-12-18 04:45:46');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','93','f0a5b6cfc72ba2fe742ad7ea11ff5fdb.png','北汽新能源','BAIC BJEV','自主','中国','北京新能源汽车股份有限公司（简称“北汽新能源” ）创立于2009年，是由世界500强企业北京汽车集团有限公司发起并控股，是我国首家独立运营、首个获得新能源汽车生产资质、首家进行混合所有制改革、首批试点国有企业员工持股改革的新能源汽车企业。','B','2023-12-18 04:45:47','2023-12-18 04:45:47');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','78','f5d93c0073eb3f8b611b5d16521f5c59.png','北汽制造','BAW','自主','中国','北京汽车制造厂建于1953年，当时称作北京第一汽车附件厂，1958年改名北京汽车制造厂。北京汽车制造厂有限公司（Beijing Automobile Works ，缩写 BAW），简称北汽，是中国北京的汽车制造商。北汽生产轻型越野车和卡车，产品涵盖民用和军用汽车产品。','B','2023-12-18 04:45:49','2023-12-18 04:45:49');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','60','f6e1139b1debc6e83e4e1d249648c356.png','北汽昌河','BAIC CHANGHE','自主','中国','江西昌河汽车有限责任公司是直接隶属中国长安汽车集团股份有限公司的全资子公司，拥有景德镇、九江、合肥三个整车工厂和九江发动机工厂，具备年产30万辆整车和15万台汽车发动机的生产能力。代表产品主要包括：昌河Q35、昌河Q25、利亚纳系列、派喜、北斗星系列、浪迪系列、爱迪尔、福瑞达系列、福运、K系列发动机等。','B','2023-12-18 04:45:49','2023-12-18 04:45:49');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','444','7245bc4abc53174fa3fd3dc5592a0102.png','北汽瑞翔','Beiqi Group Yx Auto Mobile','自主','中国','北汽瑞翔实为原北汽银翔汽车有限公司破产重整后更名，由重庆市政府、合川区政府和北汽集团联合打造的新品牌。','B','2023-12-18 04:45:51','2023-12-18 04:45:51');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','94','bc6a3ef7e6b6ef8426c24fb51ed936c1.png','北汽幻速','BAIC','自主','中国','北汽幻速是依托北汽集团大自主战略框架、为实现大北汽的宏伟战略目标、由北汽集团西南基地全力打造的独立乘用车品牌，是北汽集团旗下一个重要的产业布局及组成部分。北汽幻速品牌于2014年3月31日正式发布，旨在为消费者提供更高品质、多功能的产品，为用户驱动美好生活。','B','2023-12-18 04:45:52','2023-12-18 04:45:52');
