INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','69','a2d23bf4d2ffb4241611b02933eb57ce.png','北汽威旺','WEVAN','自主','中国','北汽威旺是北汽集团推出的一款全新的微客产品品牌。以“威旺”命名，取意“威天下、旺未来”。 北汽威旺定位于微客车型，公司隶属于北汽集团。以“威旺”命名，表达“威天下、旺未来”。','B','2023-12-18 04:45:53','2023-12-18 04:45:53');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','116','bc5c3108cd9ea924abd14c4c2d7865eb.png','宝沃','BORGWARD','德系','德国','德国老牌豪华品牌Borgward在日内瓦车展现场宣布品牌复兴，发布会上Borgward也公布了其今后在中国市场的中文名称"宝沃"，其中"宝"同"BORG"，寓意珍贵和地位，"沃"同"WARD"寓意为丰盛、润泽和富饶，合二为一，取"物华天宝，沃壤千里"之意。这是继1949年Hansa 1500在日内瓦车展亮相以来，这一德国汽车品牌首次重返日内瓦车展舞台。','B','2023-12-18 04:45:53','2023-12-18 04:45:53');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','118','4eebebc8938fe0c3c2a2e27bb110631f.png','博速','Brabus','德系','德国','博速（德语：Brabus GmbH），是一家德国运动型汽车改装公司，成立于1977年，总部设于德国鲁尔区博特罗普。主要对梅赛德斯-奔驰进行动力学及空气力学改装。每年改装有一万多辆奔驰车，并拥有11.2万平方米的销售展示间，2万多款改装零件供客户选配。','B','2023-12-18 04:45:54','2023-12-18 04:45:54');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','117','8940732703576883a4251a769894f967.png','比速汽车','BISU','自主','中国','比速汽车是指重庆比速汽车有限公司，成立于2015年4月28日，公司位于中国重庆天顶工业园。其产品涵盖MPV、SUV、轿车、新能源多个细分领域。比速汽车自品牌成立之初便坚持“精工造好车”的品牌理念，制造致力于为消费者打造超高颜值、便捷舒适、品质无忧的精工好车。','B','2023-12-18 04:45:57','2023-12-18 04:45:57');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','452','f1326c57f8bbd4e3c2ef4ae3b1bc5cbc.png','Bowler','Bowler','英系','英国','自从 1990 年代 Drew Bowler 和他的家人在他位于德比郡的农场创办这家公司以来，一直在与捷豹路虎密切合作，2019 年，Bowler 成为捷豹路虎快速发展的特种车辆运营部门的一部分。','B','2023-12-18 04:45:57','2023-12-18 04:45:57');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','459','ea4c3707289a914acebe8cc9f5156743.png','百智新能源','Baizhi New energy','自主','中国','百智新能源汽车是集纯电动汽车研发、生产、销售、服务于一体的新能源汽车制造、未来出行生态的创新型科技企业平台。','B','2023-12-18 04:45:58','2023-12-18 04:45:58');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','507','87688254e25de1b8c8289619a81afc56.png','宝能汽车','baoneng','自主','中国','宝能汽车集团有限公司成立于2017年3月，总部位于深圳，定位为国内领先、国际具有影响力的综合性汽车产业集团。','B','2023-12-18 04:45:58','2023-12-18 04:45:58');
