INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','217','d918f5c521b369c56cf1eca3ac0e2cc5.png','Corbellati','Corbellati','欧系','意大利','Corbellati由来自意大利的高端珠宝制造商Achille Corbellati创办并亲自设计，在2018年日内瓦车展上发布了Corbellati Missile超级跑车，以20世纪60年代的赛车为灵感，目标最高车速为498km/h，以此来挑战量产车的极速记录。','C','2023-12-18 04:46:20','2023-12-18 04:46:20');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','316','29527537e54202a001edcf0dcc9b70bb.png','Conquest','Conquest','其它','加拿大','Conquest汽车公司专门从事超豪华，全副武装，手工制作的运动型多用途车的设计与制造。总部设在加拿大多伦多，旗下车型多采用炭纤维和铝合金打造车身，散发出暗黑军事型格。','C','2023-12-18 04:46:21','2023-12-18 04:46:21');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','228','ca01af3ca0d9e1fd8f25a2e6583f90d5.png','Caterham','Caterham','其它','英国','Caterham是英国专业轻型跑车制造商，创立于萨里郡卡特汉姆，总部设在苏赛克斯的克劳利。2011年4月27日，Lotus车队老板托尼・费尔南德斯宣布他购买了Caterham。','C','2023-12-18 04:46:21','2023-12-18 04:46:21');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','345','c7223fcfc5578dbf2b025f827bb0b062.png','Canoo','Canoo','美系','美国','Canoo是一家中资参与的美国电动汽车公司，此前曾叫Evelozcity。该公司的概念是生产负担得起的商用电动汽车，例如用于车辆租赁和乘车共享服务的小型货车。2019年9月24日，Canoo发布了它的第一款电动汽车Cancoo。','C','2023-12-18 04:46:21','2023-12-18 04:46:21');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','376','36f8c07164cb84cc71a33ca6548e4ff2.png','Czinger','Czinger','美系','美国','Czinger是一个美国初创超跑公司，其总部位于美国洛杉矶。创始人为Kevin Czinger。2020年2月，该公司正式公布了旗下首款超跑Czinger C21的车型官图 ，这款新车将于日内瓦车展全球首发。','C','2023-12-18 04:46:22','2023-12-18 04:46:22');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','458','20256f5b3b7a2b39d36d9790f5bf7438.png','Continental','Continental','德系','德国','Continental-Caoutchouc- und Gutta-Percha Compagnie 作为股份制公司在德国汉诺威成立。在位于 Vahrenwalder 大街的主厂房内生产各种软性橡胶制品、橡胶布以及马车和自行车的实心轮胎。','C','2023-12-18 04:46:22','2023-12-18 04:46:22');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','1','3cf5ae3401c4fb990f487922b7c9b5c9.png','大众','Volkswagen','德系','德国','大众汽车（德语：Volkswagen，缩写VW），是一家总部位于德国沃尔夫斯堡的汽车制造公司，为大众集团的核心企业及原始品牌，也是该集团最畅销品牌及全球第一大汽车制造商。
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','70','8d2eec14fdfaa329b7d3f11212b133e2.png','东风风行','FORTHING','自主','中国','东风风行，是东风柳州汽车有限公司的子品牌，东风柳州汽车有限公司（以下简称东风柳汽），是东风汽车公司在南方重要的载货汽车和轻型乘用汽车生产基地，国家大型一档企业。东风风行作为有责任担当的民族品牌，在夯实品质的同时，承初心，践使命，始终将消费者需求放在首位，用心，让用户享受每一次出行。','D','2023-12-18 04:46:31','2023-12-18 04:46:31');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','37','852dabf79a3bac8478e68ca9de3d74df.png','东风风神','Dong Feng Aeolus','自主','中国','东风汽车集团旗下自主中高端品牌，创立于2009年。在东风自主乘用车事业中居于核心地位；东风风神产品开发标准全面对标主流合资品牌，汇集东风集团最优产品研发资源，在产品布局实现了SUV 轿车 新能源三线并进；2019年，东风风神与中国国家乒乓球队开展战略合作，助力品牌向上，塑造更加年轻、时尚的全新品牌形象。2020年，东风风神正式发布AEOLUS赛道标识，品牌迈向年轻化。','D','2023-12-18 04:46:32','2023-12-18 04:46:32');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','54','9120a74efb97432f11faa610e4b7e03b.png','道奇','Dodge','美系','美国','道奇（Dodge）――克莱斯勒汽车公司旗下品牌之一。其中，道奇轿车素以价廉和大众化称著, 颇受欢迎。车型号有: 蝰蛇(Viper), 无畏(Interpid), 隐形(Stealth), 小精灵(Spirit), 影子(Shadow), 霓虹(Neon)等。','D','2023-12-18 04:46:33','2023-12-18 04:46:33');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','55','529d954bf683e47311dfc505ff4ef5c3.png','DS','DS','法系','法国','1955年，DS于巴黎车展首次亮相就以设计和技术上的创新引起了轰动，并引发了汽车业的革命，在汽车发展史上有着非常重要的地位。DS极受法国政要的欢迎，被誉为“总统座驾”。','D','2023-12-18 04:46:34','2023-12-18 04:46:34');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','77','6ec0119eb7af4d516d3993ced3aaa47a.png','东南','SOUEAST','自主','中国','东南（福建）汽车工业有限公司，简称东南汽车于1995年11月23日诞生在福建省福州市，东南汽车目前产品线已完整覆盖轿车、SUV、商用车等多个领域。东南汽车现有SUV系列： “东南DX7”、“东南DX7 Prime ”、“东南DX3”、 “东南DX3SRG”，轿车系列：“东南新V5 ” 、“东南V CROSS”、"东南V3菱悦"，商用车系列："东南得利卡"等系列车型。','D','2023-12-18 04:46:36','2023-12-18 04:46:36');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','95','4b805c59ba6da7df84dc41be8a347aeb.png','东风风光','dffengguang','自主','中国','东风风光成立于2008年，是东风汽车品牌旗下的乘用车子品牌，共享东风国际资源大平台。传承东风精湛的造车技术，融合中、意、德、韩等专家团队智慧，以先进设备和制造水平，带来世界级经典品质。','D','2023-12-18 04:46:37','2023-12-18 04:46:37');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','891','ffd5a94d7c742a161fe1c1244185fc50.png','东风奕派','','','','','D','2023-12-18 04:46:37','2023-12-18 04:46:37');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','79','bf315aa0b4aff574427ff9bfd6507bad.png','东风小康','DFSK','自主','中国','东风小康是东风小康汽车有限公司旗下品牌，创立于2003年，属于重庆小康工业集团和东风汽车公司，旗下拥有东风小康、东风风光两大汽车品牌。2020年，小康股份与东风公司深化战略合作，公司成为小康股份全资子公司。自2005年全球上市以来，以其时尚、美观的“外在”优势和实用、节能的“内在”优势畅销中国各地及全球40多个国家与地区。2007年“东风小康”系列微型汽车通过国际权威机构的全面审核，在国内微型汽车行业中率先获得欧盟国家技术标准认证，标志着“东风小康”汽车的品质达到了国际标准。','D','2023-12-18 04:46:38','2023-12-18 04:46:38');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','246','8bd5a7e99be86b52a3ecd5d12978af40.png','大乘汽车','dorcen','自主','中国','大乘汽车是吴潇创立的全新中国自主汽车品牌，隶属于的江西大乘汽车有限公司的控股，股东是大乘汽车集团，江铃汽车集团是江西大乘汽车有限公司的重要股东之一；其车系以年轻时尚的定位涵盖乘用车、商用车以及新能源汽车。','D','2023-12-18 04:46:49','2023-12-18 04:46:49');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','197','1a5de751f82392a016025e4b52512228.png','电咖','Dearcc','自主','中国','电咖汽车属于浙江电咖汽车有限公司（现已更名为天际汽车科技集团有限公司）旗下品牌，成立于2015年，是一家具备互联网思维，集纯电动汽车研发、制造、销售、服务于一体的新能源汽车企业。','D','2023-12-18 04:46:50','2023-12-18 04:46:50');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','91','d50aa957d1bdda74a7786836c283cc62.png','东风','dongfeng motor','自主','中国','东风汽车集团有限公司，原名中国第二汽车制造厂，曾称东风汽车公司，是一家中国大陆汽车制造商。主要生产和组装客车、卡车、轿车和汽车零部件，使用自己的品牌或者使用与其合资的国际性品牌。','D','2023-12-18 04:46:50','2023-12-18 04:46:50');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','92','d50aa957d1bdda74a7786836c283cc62.png','东风风度','dongfengfengdu','自主','中国','东风风度品牌是东风汽车品牌旗下的乘用车子品牌，与东风风神、东风风行、东风风光一起，成为东风乘用车四大子品牌，共享东风国际资源大平台。“东风风度”品牌在商品开发、管理、车型、平台等多个层面均得到合资公司股东双方日产和东风的大力支持。','D','2023-12-18 04:47:02','2023-12-18 04:47:02');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','417','6642ad640fdd4aca2e097456d3af677c.png','东风纳米','DFPV','自主','中国','东风纳米是东风汽车集团旗下自主新能源乘用车事业板块，着力于整合集团优势发展入门级、中级轿车以及SUV等新能源汽车的商品企划、整车销售、服务业务。东风纳米汽车车系EX1上市，新车目前推出4款车型，后续逐步推出C级乘用车、中级SUV。','D','2023-12-18 04:47:03','2023-12-18 04:47:03');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','230','4faeba26ca42d2e9db9c90d2c1a67bcc.png','东风・瑞泰特','DF-RTT','自主','中国','东风汽车股份有限公司，创立于1999年7月15日，是肩负东风汽车公司轻型商用车事业的大型股份制企业。东风o瑞泰特是东风汽车股份新能源事业部推出的全新子品牌，创立于2017年，以“经济o高效o环保o安全”为品牌发展愿景，是东风汽车股份追求节约能源科学发展的战略品牌。','D','2023-12-18 04:47:04','2023-12-18 04:47:04');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','373','92ffc7768f1b296796ff98f981677c08.png','东风富康','DONGFENG PEUGEOT CITROEN AUTOMOBILE COMPANY LTD','自主','中国','作为一个深受中国消费者信赖的品牌，20多年前富康(参数|图片)引领中国消费者进入家轿时代，风靡一时创造了辉煌的历史。20多年后处于全新的时代，全新的富康破茧重生，在新能源汽车大浪潮下，东风富康开始了一段全新且艰难的旅程，承载着万千家庭的梦想，重新扬帆起航。','D','2023-12-18 04:47:05','2023-12-18 04:47:05');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','372','83652931c7799d2e1d84acf31729422a.png','大运','dayunmotor','自主','中国','大运集团前身为1987年山西通达集团有限公司，为中国大陆一间大型机械车辆公司，生产是摩托车、电动车、商用卡车，另也参与房地产开发，旗下拥有25个全资子公司、3个分公司、1个控股公司。其董事长远勤山曾当选山西省政协委员。','D','2023-12-18 04:47:06','2023-12-18 04:47:06');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','451','dadf38601985dbade3c123bd0cfca143.png','电动屋','Electric house','自主','中国','电动屋品牌以年轻群体高性价比为服务宗旨，集纯电车型产品、24小时用户服务、跨界IP合作、沉浸式用户体验等于一体，打造全新的纯电生活圈儿。','D','2023-12-18 04:47:06','2023-12-18 04:47:06');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','121','0410e2f4f78020b9e8ae3aaf783ce21f.png','大发','DAIHATSU','日系','日本','大发是“大发工业株式会社”的简称。大发工业株式会社(haobc)，1907年创立于日本大坂，百年来始终致力于小型车领域的拓展，在发动机、空间、车辆安全及环境保护等方面都拥有先进的技术，是全球领先的小型车制造企业，被誉为“小型车专家”。作为丰田集团的一员，专门生产低油耗、省资源、省空间的小型车，并以成为“真正的国际企业”为目标，积极推动全球化。','D','2023-12-18 04:47:07','2023-12-18 04:47:07');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','183','4b9ce3b5a659f734f33404ca196aad0c.png','Dacia','Dacia','法系','法国','Dacia达西亚是与罗马尼亚的汽车工业一起成长起来的汽车品牌，成立于1966年，是罗马尼亚目前最大的汽车制造企业。与雷诺汽车的合作是达西亚快速发展的开始。1999年，达西亚正式成为雷诺集团的一份子，使得达西亚成为了一个国际性品牌。','D','2023-12-18 04:47:07','2023-12-18 04:47:07');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','291','77b50a537d628318b161dded68fd9489.png','Datsun','Datsun','日系','日本','Datsun（达特桑）原本是日产汽车公司1932年生产的一款小车的名字，经过几十年的换代和发展，已成为声名显赫的著名汽车品牌。在日本车跟美国车的竞争中，它立下了汗马功劳。','D','2023-12-18 04:47:08','2023-12-18 04:47:08');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','337','5ac807e8b8c6a78fb1208564dcbdd6fd.png','De Tomaso','De Tomaso','欧系','意大利','De Tomaso Automobili是一家老牌的意大利汽车品牌，以生产超级跑车闻名。曾用名De Tomaso Modena SpA，是由阿根廷车手Alejandro de Tomaso在1959年在意大利创办，2014年被中国香港财团Ideal Team Ventures Limited收购。','D','2023-12-18 04:47:08','2023-12-18 04:47:08');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','294','a3e986dc4765f5bcdd59a70270bfb367.png','Donkervoort','Donkervoort','欧系','荷兰','Donkervoort（敦克沃特）是一家总部位于荷兰的高性能跑车制造商，以手工生产造型复古且赛道性能出色的限量版跑车闻名，他们生产的跑车没有ABS、ESP或者转向助力这种电子辅助装置。','D','2023-12-18 04:47:09','2023-12-18 04:47:09');
