INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('2','295','b8bbf13cf9e8d98e26b8931766c9c7b4.png','DeLorean','DeLorean Motor Company','美系','美国','DMC是德罗宁汽车公司（DeLorean Motor Company）的缩写，由约翰・德罗宁于1975年创立。它以生产与众不同具有鸥翼车门和不锈钢车体的德罗宁DMC-12汽车闻名于世。《回到未来系列》电影将外观独特的德罗宁DMC-12作为时间旅行机器，捧红了该车型。','D','2023-12-18 04:47:10','2023-12-18 04:47:10');
