INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','321','201a9cd74153ce465940623e23601bf3.png','GTA','GTA','欧系','西班牙','GTA是一家位于西班牙巴伦西亚的汽车设计和制造公司。它由现任公司首席执行官Domingo Ochoa创立。他们在内部设计，开发和生产了GTA Spano。','G','2023-12-18 04:47:46','2023-12-18 04:47:46');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('2','283','6ca6247dc9989e32aae7342fa5f293a0.png','Gumpert','Gumpert','德系','德国','Apollo Automobil（旧称：Gumpert Sportwagenmanufaktur）是一家德国跑车制造商，曾担任奥迪运动总监的Roland Gumpert（罗兰・甘珀特）于2004年创立了Gumpert Sportwagenmanufaktur，旗下超级跑车Apollo性能出色，奠定了品牌名称。','G','2023-12-18 04:47:46','2023-12-18 04:47:46');
