INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','342','90f478f8a097c40e62af8a8c980ecff6.png','谷歌','Google','美系','美国','Google Driverless Car是谷歌公司的 Google X 实验室研发中的全自动驾驶汽车，不需要驾驶者就能启动、行驶以及停止。','G','2023-12-18 04:47:47','2023-12-18 04:47:47');
