INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','423','299312d37d9ccd2dd39fa87d1fe380b0.png','G-POWER','G-POWER','德系','德国','德国著名汽车改装品牌，成立于1983年，总部设在Aichach，主要从事汽车的调校和制造精品车，作为宝马、奔驰的改装厂商，经其改装的汽车也十分深受世界各地汽车改装发烧友的喜爱。','G','2023-12-18 04:47:48','2023-12-18 04:47:48');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('0','433','aef664d22a49716b1097e84f7b640cb5.png','GUNTHER WERKS','GUNTHER WERKS','美系','美国','美国著名汽车改装品牌，主要从事部分保时捷车型的改装，GUNTHER WERKS所打造的993成为了复古玩家的梦幻车型。','G','2023-12-18 04:47:48','2023-12-18 04:47:48');
