INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','303','24022d05f12a7949be11130a3a89c61c.png','合创汽车','HYCAN','自主','中国','合创汽车成立于2018年主要从事纯电动汽车的研发、销售及服务，并将在产业链上下游展开全面合作，包括新能源汽车整车研发、销售服务、租赁运营以及车联网和自动驾驶等领域。融合创建全球高水平研发团队。以“合创”理念，得到广汽和蔚来包括北京、上海、硅谷等全球的广汽汽车技术和蔚来车联网的研发团队支持，还获得广汽研究院、广汽新能源智能生态工厂及NIO全球基地强有力的研发试验支撑。','H','2023-12-18 04:47:52','2023-12-18 04:47:52');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','53','92e813c24ebaa598fbf8615039fd9607.png','海马','HAMA','自主','中国','1988年，海马建厂于海南省海口市。在新品研发方面，海马汽车始终秉承“在开放中寻求合作、在合作中谋求发展、在发展中确立自我”的战略方针，充分利用国内外合作伙伴的先进技术，结合自身优势，详尽规划了近、中、远期产品的发展战略，纳入具体规划的新产品达到16个。','H','2023-12-18 04:47:53','2023-12-18 04:47:53');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','126','83ad94d2dfcdac5ead1d8504d4ca80d8.png','汉腾汽车','HANTENG','自主','中国','汉腾汽车有限公司（简称“汉腾汽车”，英语：Hanteng Autos），成立于2013年11月，位于江西省上饶市经济技术开发区，是一家全新的以传统动力汽车、新能源汽车、关键汽车零部件的研发、生产、销售为核心业务的民营整车制造企业。','H','2023-12-18 04:47:55','2023-12-18 04:47:55');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','413','d02d91c12342ce929cd7e41d56452bdd.png','华晨新日','Huachen Xinri','自主','中国','华晨新日公司经营范围包括：研发、生产、销售新能源汽车零部件、汽车饰品及其售后服务；新能源汽车的研发、销售、租赁（不含融资租赁）及售后服务；新能源汽车的生产（仅供办理申请国家公告目录使用，不得开展生产经营活动）；自营和代理各类商品及技术的进出口业务（国家限定公司经营或禁止进出口的商品和技术除外）等。','H','2023-12-18 04:47:55','2023-12-18 04:47:55');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','72','1ba54da8d031ffc1be7888d3afa823e5.png','华泰','HAWTAI','自主','中国','华泰汽车集团是以新能源汽车业务为核心，集整车和动力总成研发、设计、制造、销售以及汽车金融为一体，多元化发展的大型跨国集团，是国内唯一拥有自动变速器和世界领先清洁汽、柴油发动机核心技术完全自主知识产权。','H','2023-12-18 04:47:56','2023-12-18 04:47:56');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','99','1ba54da8d031ffc1be7888d3afa823e5.png','华泰新能源','HAWTAI','自主','中国','华泰新能源已开发了六个整车平台，包括小型电动车、轿车、SUV、客车、MPV和电动物流车，未来三年，仅新能源领域就将有超过10款新车投放市场，其中包括2018年将推出混合动力新能源车型。2019年，华泰汽车完全自主研发MPV纯电动车也将上市。','H','2023-12-18 04:47:56','2023-12-18 04:47:56');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','399','7aade3d8b1e08b45a1365ab2d0a38d51.png','恒驰','Hengchi','自主','中国','恒驰新能源汽车，于2019年8月28日横空出世。2020年8月3日，恒大发布恒驰首期六款车，完全覆盖了A到D级所有级别，和轿车、轿跑、SUV、MPV、跨界车等全系列车型，实现产品线全覆盖，用时两年走完其在新能源汽车领域“从0到1”全过程。','H','2023-12-18 04:47:57','2023-12-18 04:47:57');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','131','83eba0e810209c637359153a8522a570.png','华颂','huasong','自主','中国','2014年，华晨汽车发布新品牌――华颂，是华晨汽车基于与国际工程及设计公司的深度合作，以“芯”的力量整合全球造车精湛技艺，所推出的合作自主品牌。','H','2023-12-18 04:47:57','2023-12-18 04:47:57');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','132','7426315724b0d098019d5795c363c242.png','黄海','HUANGHAI','自主','中国','辽宁曙光汽车集团股份有限公司（英文简称SG Automotive Group，中文简称曙光汽车集团，股票代码600303），是以整车、车桥及零部件为主营业务的跨地区的企业集团，是“国家汽车整车出口基地企业”，拥有国家级技术中心。','H','2023-12-18 04:47:57','2023-12-18 04:47:57');
