INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','98','8f3c62482b1c2e0ba2a841a920d74039.png','哈飞','HAFEI','自主','中国','哈飞汽车，全称哈尔滨哈飞汽车工业集团有限公司是一家中国汽车公司。2009年成为长安汽车子公司。','H','2023-12-18 04:48:01','2023-12-18 04:48:01');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('2','130','4a90426dc27418fbaf44fb954ede4ddb.png','华普','SMA','自主','中国','上海华普汽车是中国吉利控股集团有限公司的子公司，生产乘用机动车。','H','2023-12-18 04:48:02','2023-12-18 04:48:02');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','220','516b94c707e7c952d589930dec30ddb1.png','Hennessey','Hennessey','美系','美国','轩尼诗（Hennessey Performance Engineering）由约翰・轩尼诗（John Hennessey）在1991年创立，是美国一家以改装跑车而闻名的汽车改装公司，曾为法拉利、保时捷、迈凯轮、雪佛兰、道奇、凯迪拉克、莲花、吉普、福特、GMC、林肯和雷克萨斯等多个品牌的跑车做专业改装。除了高性能跑车之外，还为皮卡如福特猛禽（Ford Raptors），越野车如吉普大切诺基（Jeep Grand Cherokees）做专业改装。他们还从事宾利汽车等豪华汽车以及道奇Charger和Challenger等肌肉汽车的开发。','H','2023-12-18 04:48:04','2023-12-18 04:48:04');
