INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','437','2576895e2137870a3f2080b367ca61ac.png','HOFELE','HOFELE','德系','德国','Hofele Design是一家德国著名改装厂，于1983年在德国格平根区德茨道夫成立，主要从事梅赛德斯-奔驰汽车改装。','H','2023-12-18 04:48:09','2023-12-18 04:48:09');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','403','92f214eb236a00c6fe30af8168902e43.png','Hyperion','Hyperion','美系','美国','Hyperion汽车是由美国硅谷一群物理学博士专家团队组建的，致力于研发基于氢燃料电池技术的高性能新能源跑车和新能源航天器，并与NASA开展广泛的技术合作。他们希望能够给世界带来清洁的、可再生的氢能源未来。','H','2023-12-18 04:48:09','2023-12-18 04:48:09');
