INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','165','3a9427edb72a5e2174386f42c852e744.png','Icona','Icona','其它','意大利','ICONA意柯那设计集团公司于2010年初在意大利都灵成立，并在中国上海和美国洛杉矶设有办公室。意柯那拥有经验丰富的专业团队，并能整合利用自身及外部资源，为客户提供全方位服务，包含：造型设计，模型及样车试制，可行性分析，以及对应的舒适性测试人机工程验证。','I','2023-12-18 04:48:10','2023-12-18 04:48:10');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','223','2341100b9fcdaf149d296bb36256e4c0.png','Italdesign','Italdesign','其它','意大利','Italdesign一家意大利老牌汽车设计和工程公司，由乔治・乔治罗亚（Giorgetto Giugiaro）和奥尔多・曼托凡尼（Aldo Mantovani）于1968年创立。','I','2023-12-18 04:48:11','2023-12-18 04:48:11');
