INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','388','e79c4e8d2679ac4bdde78b10ae599610.png','IED','IED','欧系','意大利','欧洲设计学院（Istituto Europeo di Design，简称IED），1966年Francesco Morelli在意大利米兰建校，学院总部设在意大利米兰，在都灵、罗马、卡利亚利等城市分别设有分院。','I','2023-12-18 04:48:11','2023-12-18 04:48:11');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','387','3e82cc130f826881284e2c484cbce424.png','INKAS','INKAS','其它','加拿大','INKAS是一家加拿大私人控股公司，专门从事安全，制造和开发服务。INKAS装甲车制造公司是INKAS集团公司的分支机构，专门从事各种装甲车的设计和生产，包括行政SUV，豪华轿车，专用车，人员运输车，在途现金车辆等。','I','2023-12-18 04:48:11','2023-12-18 04:48:11');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','393','1c2f81b9619ece83c15dbc6f5393bb1e.png','INEOS','INEOS','其它','未知','INEOS英力士是英国的一家大型石化集团企业，总部位于瑞士罗勒。2017年，企业董事长Jim Ratcliffe成立了英力士汽车公司（Ineos Automotive Ltd.），INEOS Grenadier是旗下最新的一款越野车产品，预计2021年12月开始生产，并于2022年投入市场。','I','2023-12-18 04:48:12','2023-12-18 04:48:12');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','398','dae50440e273f52d284fbadcf47fda5e.png','IZERA','IZERA','其它','未知','IZERA汽车波兰公司ElectroMobility Poland（EMP）旗下电动汽车品牌。波兰EMP公司希望通过与大众、丰田和现代等汽车公司签署授权协议，使用成熟技术平台来生产IZERA电动汽车。','I','2023-12-18 04:48:12','2023-12-18 04:48:12');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','488','6e4886c81677dc8f9bbd494e762be000.png','INDI','INDI','美系','美国','INDI正在创造一种能够满足您更多日常需求的车辆。不仅仅是一种从 A 移动到 B 的模式，一旦您到达那里，您的车辆将使您能够做的事情将使您的生活更轻松、更有效率。','I','2023-12-18 04:48:13','2023-12-18 04:48:13');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','73','edaaa3a90084af099f1eb8aaec147b52.png','吉利汽车','GEELY AUTO','自主','中国','吉利汽车集团隶属于浙江吉利控股集团，总部位于中国浙江杭州，在浙江台州/宁波、湖南湘潭、四川成都、陕西宝鸡、山西晋中等地建有汽车整车和动力总成制造基地，并在白俄罗斯等国家和地区建有海外工厂。吉利汽车集团是中国领先的汽车制造商，立志成为最具竞争力和受人尊敬的中国汽车品牌。吉利汽车集团旗下现拥有吉利汽车品牌、领克品牌和几何品牌。','J','2023-12-18 04:48:13','2023-12-18 04:48:13');
