INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','354','786a5eaf0a6a64c0ae59399060532f60.png','Jannarelly','Jannarelly','其它','未知','Jannarelly是一家由法国设计师Anthony Jannarelly创立的定制跑车品牌，生产工厂位于中东阿联酋迪拜。Anthony Jannarelly同时也是W Motors汽车公司Lykan HyperSport的设计师。','J','2023-12-18 04:49:12','2023-12-18 04:49:12');
