INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','102','2ad8668d581d1352281f0ad652034bde.png','陆风','LINDWIND','自主','中国','陆风汽车由江铃控股有限公司在国家新的汽车产业政策的引导和支持下，于2004年11月由长安汽车股份有限公司和江铃汽车集团公司通过强强联合，实现中、中合作的国内首家汽车制造企业。拥有江铃控股本部拥有两大生产基地：昌北基地与小蓝轿车基地。','L','2023-12-18 04:53:12','2023-12-18 04:53:12');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','111','94b79c1e971c2c2cf9e77dde365c25b5.png','理念','EVERUS','自主','中国','2007年7月19日，广汽本田成立了广汽本田汽车研究有限公司。开展了商品企划、造型、设计、试作、试验部门的车型开发业务。2008年4月20日，广汽本田正式发布理念品牌。广汽本田通过理念品牌的发展，建立并强化了公司的研发、制造及营销体制，并使得已有的制造及营销功能得以拓展，能够与研发进行联动，实现了由汽车制造工厂向完整汽车企业的转变。作为广汽本田自主研发的品牌，理念也将传承Honda和广汽本田的企业文化，在不远的将来通过产品给顾客带来充满“喜悦”的、时尚动感的优质生活。','L','2023-12-18 04:53:15','2023-12-18 04:53:15');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','282','26b0b872b42eabd450ebdce15941cd17.png','雷丁','levdeo','自主','中国','雷丁是国内高端自主研发电动汽车品牌，自2008年开始专注于电动汽车整车及关键零部件的研发及生产制造，目前已形成电动汽车整车及关键零部件的研发、制造、销售、售后服务体系。','L','2023-12-18 04:53:16','2023-12-18 04:53:16');
