INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','145','d8da9cd79badbb41f920598f1ab14139.png','Lorinser','Lorinser','德系','德国','“LORINSER”对车身进行重新设计从而获得完美的车身结构和美学感，使其更具豪华的张力。早在二战前1935年LORINSER在德国斯图加特已经成立了，比“AMG”、“BRABUS”的创立时间还要早，可以说是改装业的“老师傅”了。','L','2023-12-18 04:53:17','2023-12-18 04:53:17');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','248','d46455cd3cfc0357215603b396ef33d2.png','罗夫哈特','Rolfhartge','德系','德国','罗夫哈特Rolfhartge站在最专业的角度，用最极致的改装；选最顶级的车型为客户打造出最完美的订制座驾。对于改装的工艺的苛刻要求让罗夫哈特Rolfhartge的每一款车型都如工艺品般精雕细琢。','L','2023-12-18 04:53:19','2023-12-18 04:53:19');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','247','3670d17f60146e985f03d5375bbe0120.png','领途汽车','LINK TOUR','自主','中国','领途汽车有限公司，是御捷联手长城汽车及国内多家机构投资，主要从事新能源汽车制造、未来出行生态的一家创新型科技企业。领途汽车，专注小型纯电动汽车发展，业务覆盖新能源汽车整车研发、生产、销售、服务、以及共享汽车等领域，致力成为全球小型纯电动汽车细分品类领导品牌。','L','2023-12-18 04:53:19','2023-12-18 04:53:19');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','146','57d127db4632618f0bcefcaff69f2795.png','陆地方舟','Green Wheel','自主','中国','陆地方舟新能源电动车集团是是最早自主从事电动汽车核心技术研发及生产的国家高新技术企业，经过十几年对电动汽车核心技术坚持不懈的自主研发，目前除自主拥有包括“三大电、三小电”在内的电动汽车的三大核心技术和九项关键技术外，还积累了丰富的产业化技术和经验。','L','2023-12-18 04:53:19','2023-12-18 04:53:19');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','415','5c10367e6ba16aa439187ceadc82cdee.png','龙程汽车','long cheng qi che','自主','中国','龙程汽车是高碑店市龙程汽车制造有限公司，于2005年09月29日成立。曾叫做涿州龙程汽车有限公司和河北五湖专用汽车制造有限公司，是一家民营汽车改装厂，拥有“众骄牌”注册商标。','L','2023-12-18 04:53:20','2023-12-18 04:53:20');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','467','7e0c883c8decb34d4bc99f3b155997de.png','莱茵汽车','Rhine Auto','自主','中国','立足河南，辐射全球为企业目标，以“源于德国、品质至上”为品牌理念,莱茵汽车是中原地区符合梅赛德斯奔驰标准的专用车制造工厂,正在为逐步实现“梅赛运贡汲凼澜缂蹲ㄓ贸抵圃旃こА钡脑妇岸不懈努力。','L','2023-12-18 04:53:21','2023-12-18 04:53:21');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','517','098433753f19856549f9c099bd714de4.png','Lightyear','Lightyear','其它','荷兰','Lightyear是荷兰的一家电动汽车初创公司太阳能汽车公司，是由荷兰埃因霍芬理工大学学生在2016年发起的太阳能电动汽车项目。','L','2023-12-18 04:53:21','2023-12-18 04:53:21');
