INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('2','352','f0041cabcb425262343f5d9f35e0a3da.png','罗孚','Rover','英系','英国','罗孚汽车公司是英国一家老牌汽车公司，创立于1877年，7年后率先推出安全型自行车，当时的广告语“罗孚让世界充满时尚”激发了众多消费者的购买。车系有200车系、400车系、600车系、800车系、MINI车系、吉普车系、发现者车系等。','L','2023-12-18 04:53:23','2023-12-18 04:53:23');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','384','3a029d91ec64c9196cc1693281fec6b2.png','凌宝汽车','LINGBOX','自主','中国','凌宝是金彭集团旗下吉麦新能源推出的首个新能源汽车品牌。凌宝汽车由苏北地区的智慧型新能源汽车主机厂――吉麦新能源车业有限公司提供技术支持，并与国际汽车设计公司建立紧密的整车研发合作关系，成立汽车研究院，自主掌握电机、电控、电池智能管理等核心技术。','L','2023-12-18 04:53:24','2023-12-18 04:53:24');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('2','277','eb600484f7af57a143ba47b7e79e2011.png','蓝旗亚','LANCIA','欧系','意大利','蓝旗亚（LANCIA）是菲亚特集团旗下的品牌之一，以生产豪华轿车为主。该品牌于1906年由菲亚特的赛车手文森佐・蓝旗亚（Vincenzo Lancia）创立，1969年被菲亚特集团收购，并专注于高档轿、跑车的生产，其产品在欧美各国受到欢迎。年产量约20万辆，比较著名的车型有Y型（Ypsilon）车和卡帕（Kappa）车等。','L','2023-12-18 04:53:27','2023-12-18 04:53:27');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('2','101','de6769a66535afa2858891b9292c3ef1.png','力帆汽车','LIFAN','自主','中国','力帆汽车位于重庆市汽车园，总体规划用地700亩，一期建设300亩。厂区紧靠210、212、319国道和主城环线四条高速公路，可直达国内各大、中城市，距重庆最大的寸滩集装箱码头约1km，可通往全国各通航口岸，交通十分便利。','L','2023-12-18 04:53:29','2023-12-18 04:53:29');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','412','f98dbb4d26c21e67b622a59a88d6f3f3.png','LUMMA','LUMMA','德系','德国','LUMMA Design（虏曼）是德国汽车定制改装品牌，创立于1987年，创始人Horst Lumma，主要生产定制改装车。德国LUMMA汽车公司是性化汽车生产商。为满足客户的需求，是定制改装厂商之一。','L','2023-12-18 04:53:38','2023-12-18 04:53:38');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','324','48ef3734bf317219da432581d74917f4.png','LEVC','LEVC','英系','英国','LEVC是伦敦电动汽车公司（London Electric Vehicle Company）的缩写简称，该公司是浙江吉利新能源商用车集团下的全资子公司，专注于研发生产新能源商用汽车。','L','2023-12-18 04:53:41','2023-12-18 04:53:41');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','192','a529251a88df3031d5540ec2875e6cd9.png','拉达','LADA','其它','俄罗斯','拉达是俄罗斯最大汽车制造厂DD伏尔加（AvtoVAZ）旗下的汽车品牌，2009年2月正式停产。承载了苏联和俄罗斯几代人情感的拉达，是伏尔加的当家“花旦”。在上世纪60年代苏联从意大利引进了该车型，上世纪80年代曾一度做过北京的出租车，后因外观差被夏利取代。但在莫斯科可绝对是个宝，在俄罗斯的中国人总结的俄罗斯四大怪中的第一怪就是“拉达开得比火箭快”。','L','2023-12-18 04:53:41','2023-12-18 04:53:41');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('2','43','58132ee9e085e0643dc87ac6bfdf757e.png','莲花汽车','youngmanlotus','自主','中国','在中国，“莲花汽车”指浙江青年乘用车集团有限公司生产的“青年莲花汽车”。青年莲花是青年汽车集团所有的乘用车子品牌，技术平台源自马来西亚宝腾汽车，英国莲花工程公司提供底盘调教等技术合作，青年汽车集团负责生产、销售与自主管理。','L','2023-12-18 04:53:42','2023-12-18 04:53:42');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','298','d272d29d73f2e02c9ef57411a03dde47.png','LeSEE','LeSEE','自主','中国','乐视超级汽车的品牌名称正式定名为“LeSEE”。Le代表乐视，SEE代表“乐视超级电子生态”（Super Electronic Ecosystem），具体品牌理念为电动化、智能化、互联网化、社会化，号称是“完整的汽车生态系统”，这是驱动乐视造车的核心理念。','L','2023-12-18 04:53:43','2023-12-18 04:53:43');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','212','f1acb1c4f0d319d8bb2b3d949e37085f.png','绿驰','LVCHI','自主','中国','绿驰汽车成立于2016年，是一家以智能汽车为载体的智能移动生活运营商，秉承“共享”的发展理念，集新能源汽车研发、制造、分售、出行服务等全价值链运营于一体的的新能源汽车高新技术企业。','L','2023-12-18 04:53:44','2023-12-18 04:53:44');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','301','e32908e9ed757d25ed463913aa34fd10.png','雷诺三星','leinuosanxing','韩系','韩国','雷诺三星汽车（Renault Samsung Motors）是韩国的汽车生产商，总部位于首尔中区蓬莱洞，另有生产工场位于釜山江西区，设计中心及研究院于京畿道龙仁市，雷诺三星汽车是韩国自动车工业协会的成员之一。','L','2023-12-18 04:53:45','2023-12-18 04:53:45');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','214','d8e5a8409ee6ac54d61781658007d9d6.png','拉共达','lagonda','其它','英国','拉共达，英文名为Lagonda，是英国一家著名的豪华汽车品牌。公司于1906年在英格兰的米德尔塞克斯郡成立，创始人是Wilbur Gunn。在1947年到1976年间，拉共达制造了七辆四门轿车。','L','2023-12-18 04:53:45','2023-12-18 04:53:45');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','144','45a2f9f8dc4956872c155c32b2ec56cf.png','LOCAL MOTORS','LOCAL MOTORS','美系','美国','Local Motors汽车是美国的一家小型汽车公司，该公司提倡“为顾客设计汽车”的理念，所有的汽车产品具有前卫、风格独特的气质，采用高科技材料以及美国橡树岭国家实验室的增材制作方案（3D打印）生产，并且已经与美国制造技术协会（AMT）达成协议，共同制作3D打印电动汽车。','L','2023-12-18 04:53:45','2023-12-18 04:53:45');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','309','29ecf0115aed1abc630d4fead4c3db8c.png','领志','LEAHEAD','自主','中国','领志汽车是广汽丰田的自主品牌，英文名为“LEAHEAD”，成立于2015年。领志汽车的产品主要聚焦在新能源车型上，其首款车型为领志i1。','L','2023-12-18 04:53:46','2023-12-18 04:53:46');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('2','310','2d2967883abaf30c1f65d4e5e2d1cb94.png','朗世','Ranz','自主','中国','2013年3月12日，一汽丰田在十周年年会上发布了全新合资自主品牌――朗世（RANZ），其寓意为开启明朗乐观的人生旅程及创造人性化的汽车世界，朗世品牌主要开发纯电动汽车。','L','2023-12-18 04:53:46','2023-12-18 04:53:46');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','325','5ff0888d7b14679f17eaf484c02543b3.png','Lucid','Lucid','美系','美国','Lucid Motors（以前称为Atieva）是一家专门从事电动汽车的美国汽车公司，是华人工程师谢家鹏创立。截至2020年，其第一款汽车Lucid Air处于开发中。','L','2023-12-18 04:53:47','2023-12-18 04:53:47');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','371','d3b6c2f2c3242ccc8c9931e13befa52a.png','Lordstown Motors','Lordstown Motors','美系','美国','洛兹敦汽车公司是美国的电动汽车汽车制造商。Lordstown Motors位于美国俄亥俄州的Lordstown，厂房原属于General Motors的Lordstown Assem（GM Lordstown工厂）。','L','2023-12-18 04:53:47','2023-12-18 04:53:47');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','508','09bfe9884c0bb2c5eba79e9b8ec608ee.png','LIMGENE凌际','LIMGENE','自主','中国','LIMGENE（凌际）品牌，是国内致力于原创设计的汽车改装品牌，具有专业化汽车设计研发团队及整车设计开发经验。于2015年成立重庆改装研发中心，2021年携手鹏宇汽车取得福建奔驰授权改装资质，建立珠海整车生产中心，进军汽车高端定制市场。致力于满足客户豪华、时尚、个性、科技的定制化需求。','L','2023-12-18 04:53:47','2023-12-18 04:53:47');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','548','3d00439b80c1651360039e96971339f7.png','LIUX','','','','','L','2023-12-18 04:53:48','2023-12-18 04:53:48');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','15','8e76f2264f8604ff0aa2a9dde4d35c8d.png','马自达','Mazda','日系','日本','马自达株式会社（日语：マツダ株式会社，英语：Mazda Motor Corporation），简称马自达，是日本第五大汽车制造厂，总部位于广岛县安艺郡府中町，且一度成为全球唯一生产转子引擎市售车的车厂。','M','2023-12-18 04:53:48','2023-12-18 04:53:48');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','34','d5c66d9181af100047a0a0329a0c5335.png','名爵','MG','自主','中国','名爵的英文名称MG，取自创始人威廉・莫里斯（William Morris）在1910年创立的Morris Garages（莫里斯车行）首字母。运用了英国圣公教会天穹的极具张力、坚定、稳固的八角形形状来作为名爵的标志。这个标志散发出贵族传统的气质与灵性，更象征着热情、忠诚。后来，名爵八角形的标志一传十，十传百，大大强化了名爵对汽车爱好者的吸引力。','M','2023-12-18 04:53:51','2023-12-18 04:53:51');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','45','d774177008808265cc9e77e344958858.png','玛莎拉蒂','Maserati','欧系','意大利','玛莎拉蒂（Maserati）是一家意大利豪华汽车制造商，1914年12月1日成立于博洛尼亚（Bologna），公司总部现设于摩德纳（Modena），品牌的标志为一支三叉戟。而今的玛莎拉蒂全新轿跑系列是意大利顶尖轿跑车制作技术的体现，也是意大利设计美学以及优质工匠设计思维的完美结合。','M','2023-12-18 04:53:54','2023-12-18 04:53:54');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','65','536b08bab498eb341cfe184589723563.png','MINI','MINI','德系','英国','MINI是由英国汽车公司推出后被宝马公司持有的一款汽车品牌，该汽车品牌是一款风靡全球、个性十足的小型两厢车，1959年8月26日由英国汽车公司（BMC）推出，在半个多世纪的历史里，Mini获得了巨大的成功。2000年旧款Mini停止生产，Mini品牌的新持有者宝马（BMW）宣布推出Mini的继承车款，并将新车的品牌定为MINI（全为大写英文字母）。','M','2023-12-18 04:53:56','2023-12-18 04:53:56');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','86','a44515604a20b5a7f8773b547cb6b7bf.png','迈凯伦','McLaren','其它','英国','迈凯伦集团总部位于英国沃金 (Woking) ，聚集了先进的高科技公司，每个公司都最大化地为迈凯伦在世界一级方程式锦标赛运动领域的前沿技术中建立起的全球名声和美誉添砖加瓦，通过完全奉献、优质和杰出的实施原则，迈凯伦集团已在英国工程和技术界名列前茅。','M','2023-12-18 04:54:04','2023-12-18 04:54:04');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','349','f904d6ffc497e8b5de017be84c6434a2.png','迈迈','mai mai','自主','中国','迈迈车商户端致力于为经销商提供一个快速便捷的收车渠道，迈迈车商户端S2R竞拍模式遵循维克瑞规则，为经销商会员拓宽车源收购渠道，同时结合迈迈车线下认证店，让交易更专业更便捷。','M','2023-12-18 04:55:20','2023-12-18 04:55:20');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','272','a65182c0a2c45169269ad85d53152d48.png','迈莎锐','Mansory','德系','德国','Mansory是一个国际超级改装品牌，拥有全球顶级的碳纤工艺、全球顶级的内饰设计制造工艺、全球顶级的改装工艺，在全球范围内，他是一个当之无愧的先驱者和碳纤制造专家。迈莎锐-中国正式成立于2018年，为MANSORY官方授权旗下全系产品在中国大陆唯一战略合作伙伴。','M','2023-12-18 04:55:21','2023-12-18 04:55:21');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','147','f3d7469ef65173804b655e3266dc681f.png','摩根','MORGAN','其它','英国','摩根汽车公司是一家有100年历史的英国汽车公司。摩根汽车中最具跑车色彩的是摩根Plus8。该车的外形并无变化，但是因拥有一台184马力(135千瓦)的强大发动机，行驶起来如电闪雷鸣。摩根Plus8车身稍大些，可以让驾车者不至把胳膊伸到车厢外。','M','2023-12-18 04:55:31','2023-12-18 04:55:31');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','356','47c0444b2190cc36180ba8c415c77cde.png','敏安汽车','Minan Auto','自主','中国','敏安汽车成立于2013年，由台湾敏实集团旗下子公司展图（中国）投资有限公司和淮安开发控股有限公司合资建立。敏安汽车于2016年11月获得国家发改委的纯电动乘用车项目建设批复。2018年第四季度，敏安汽车发布了首款量产SUV车型，并命名为“澜图”。','M','2023-12-18 04:55:32','2023-12-18 04:55:32');
