INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','18','e43a2dc7f2ecf01254c98991bddc1759.png','奇瑞','CHERY','自主','中国','奇瑞汽车股份有限公司是一家从事汽车生产的国有控股企业，公司产品覆盖乘用车、商用车、微型车等领域，奇瑞汽车9年蝉联中国自主品牌销量冠军，成为中国自主品牌中的代表。奇瑞汽车目前有瑞虎和艾瑞泽两大系列，其中瑞虎系列为SUV车型，艾瑞泽系列为轿车车型。','Q','2023-12-18 04:56:07','2023-12-18 04:56:07');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','461','f32548e221f8176b6b372976b5c8abd0.png','奇瑞新能源','Chery New Energy','自主','中国','奇瑞新能源汽车技术有限公司于2010年4月正式成立，其前身为奇瑞汽车股份有限公司新能源汽车项目组，现已独立为全新品牌“奇瑞新能源”。','Q','2023-12-18 04:56:11','2023-12-18 04:56:11');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','13','1a039bf450fb67e288586b0579154bf5.png','起亚','KIA','韩系','韩国','起亚汽车株式会社（韩语：／起自榆）是现代汽车集团的子公司，财富世界500强企业，为韩国第二大汽车制造商，总部位于首尔。','Q','2023-12-18 04:56:14','2023-12-18 04:56:14');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','109','1184574f35c58d6cb432054635582887.png','启辰','Venucia','自主','中国','启辰品牌成立于2010年9月8日，是东风日产乘用车公司旗下三品牌之一，是东风日产电动化、智能化的先行军。','Q','2023-12-18 04:56:18','2023-12-18 04:56:18');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','149','11f3eb212e08cbc98c531d871abdaf19.png','前途','QIANTU','自主','中国','前途汽车创建时间是2015年，商品/服务卡车; 拖车（车辆）; 运货车; 小型机动车; 电动运载工具; 跑车; 小汽车; 汽车; 野营车; 房车。前途汽车品牌理念为：以先天下之行而行的时代精神为使命，为时代的进取者提供全新的纯电驾驶乐趣体验。','Q','2023-12-18 04:56:19','2023-12-18 04:56:19');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('2','143','66dc1de9289b76aec323be2a896d0da2.png','全球鹰','quanqiuying','自主','中国','吉利全球鹰是吉利经明确乘用车细分为的三个子品牌之一。成立于2008年11月6日，是中国汽车品牌，主要生产清洁能源动力汽车。','Q','2023-12-18 04:56:21','2023-12-18 04:56:21');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','236','49b6e38d17c7684f588608970ad932df.png','乔治・巴顿','G.PATTON','美系','美国','乔治・巴顿越野车是一款美国产超级越野车，乔治・巴顿在国内只有很少的数量。此车以二战时期美国将领乔治巴顿起名，据悉，乔治巴顿生前独爱两种车型，一个是坦克另一个就是超级大吉普。因为这两款车都有较好的续航能力和越野能力。','Q','2023-12-18 04:56:21','2023-12-18 04:56:21');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','104','64c5370917c8e34b1602af7751499637.png','骐铃汽车','JDMC','自主','中国','江西骐铃汽车销售服务有限公司是江西大乘汽车工业有限公司和江铃汽车集团公司合资打造的商用车品牌，以节能与新能源（包含商用车和乘用车）和关键零部件为主导产品，以智能制造、节能环保为载体，集研发、销售为一体的复合型、高科技产业园区。','Q','2023-12-18 04:56:22','2023-12-18 04:56:22');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','265','5f6e867a8146b0ec436e7dde9428863a.png','清源汽车','QYEV','自主','中国','天津清源电动车辆有限责任公司成立于2001年11月，是我国最早专业从事电动汽车研发的公司之一。清源公司以新能源商用车以及无人驾驶技术的研究开发为重点发展目标，快速发展并打造自己的核心竞争力，致力链合全球资源，成为国际一流的新能源汽车企业。','Q','2023-12-18 04:56:22','2023-12-18 04:56:22');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','435','77a9188c3bde72d9afa7073dbaaf6d8e.png','奇鲁汽车','cherub auto','自主','中国','奇鲁新能源汽车有限公司系山东省地方政府和奇瑞集团在多年新能源汽车生产基地合作基础上，为做大做强新能源汽车产业集群，双方再次深度合作，由地方政府全资控股并于2020年9月注册成立。','Q','2023-12-18 04:56:23','2023-12-18 04:56:23');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','10','f94c26880768698a47ea71e2c22d53df.png','日产','Nissan','日系','日本','1914年，由田建治郎等人创建的 “快进社”，于1934年改为日产汽车公司。日产公司生产的轿车品牌很多，有总统、公子、桂冠、地平线、西尔维亚、羚羊、王子、南风、紫罗兰和小太阳等。“NISSAN’是日语 “日产”两个字的罗马音形式，是日本产业的简称，其含义是 “以人和汽车的明天为引目标”。其图形商标是将NISSAN放在一个火红的太阳上，简明扼要地表明了公司名称，突出了所在国家的形象，这在汽车商标文化中独树一帜。','R','2023-12-18 04:56:23','2023-12-18 04:56:23');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','36','4dd7b3cc321fef1fc80d069e70d4b6d8.png','荣威','Roewe','自主','中国','荣威的品牌口号为"品位、科技、实现"，表达了上海汽车以国际化的视野、创新的理念传承国际汽车的先进技术，以民用车科技为核心，打造国际品牌新经典的决心和信心。','R','2023-12-18 04:56:28','2023-12-18 04:56:28');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','381','222956f4df27d1acaeb032f1ed46c754.png','睿蓝汽车','rui lan qi che','自主','中国','睿蓝汽车于2022年1月24日正式完成注册，是由吉利汽车控股有限公司和力帆科技（集团）股份有限公司双方共同出资组建的合资汽车公司。','R','2023-12-18 04:56:38','2023-12-18 04:56:38');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','198','d35421293c886b6cd708d1a0189b9a82.png','瑞驰','Ruichi','自主','中国','重庆瑞驰汽车实业有限公司于2003年09月27日成立，是首批具有国家纯电动整车生产资质的企业，公司致力于成为微型电动商用汽车领先品牌。','R','2023-12-18 04:56:38','2023-12-18 04:56:38');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','244','341352455befe8e08fa405d6ec012d45.png','容大智造','Cenntroauto','自主','中国','2017年，恒源电动汽车集团宣布推出新能源汽车制造平台――容大智造。容大智造聚合智能网联、数字化及人工智能等技术优势， 将智能制造和车联网进行有效的结合，为大型物流企业提供定制化的行业解决方案。','R','2023-12-18 04:56:39','2023-12-18 04:56:39');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','150','3a13857d67c66afad27b244f797d9bbd.png','如虎','RUF','德系','德国','RUF（中文译：如虎）位于德国境内Allgau区Pfaffenhausen镇Mindelheimer街上的RUF工厂，最早是1939年时由Alois Ruf Senior所创立的一家服务大众的汽车维修厂，后经历第二次世界大战、两德分裂直到统一，成为重装超级跑车的改造工厂。','R','2023-12-18 04:56:39','2023-12-18 04:56:39');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('2','151','5e46ed17308cf533f08db584cf7ab5e1.png','瑞麒','RIICH','自主','中国','瑞麒是奇瑞十二年在研发，技术，生产，管理等各方面积累的结果呈现，肩负着奇瑞向中高端市场进军，与各大著名全球品牌和合资品牌竞争的重任。其标志传达出蜕变、超越的向上理念，代表车型为瑞麒G5。2012年，奇瑞汽车战略结构调整，宣布逐渐停用瑞麒和威麟两个子品牌。','R','2023-12-18 04:56:39','2023-12-18 04:56:39');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','292','4ddc6651236b93c69105e4e094326047.png','Rezvani','Rezvani','美系','美国','Rezvani Motors（雷兹瓦尼汽车）是总部设在加利福尼亚州尔湾市的高性能跑车的汽车设计师和制造商。雷兹瓦尼汽车是由伊朗裔企业家、前Vencer Sarthe设计师之一Ferris Rezvani创办，此前还曾经跟阿斯顿・马丁、法拉利和大卫・布朗汽车等合作过。公司的主要产品是Rezvani Beast，是Rezvani自筹资金的项目。','R','2023-12-18 04:56:40','2023-12-18 04:56:40');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','213','6155fa4fe65eeb6af18888c7bfc5ca05.png','Rimac','Rimac','其它','匈牙利','Rimac Automobili是一家克罗地亚电动汽车制造商，并为全球汽车制造商提供完整的技术解决方案。总部位于克罗地亚的Sveta Nedelja，开发和生产电动跑车，传动系统和电池系统。该公司由Mate Rimac于2009年建立。','R','2023-12-18 04:56:40','2023-12-18 04:56:40');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','279','664ee0c89f659e2edded706d7fe3998d.png','Rinspeed','Rinspeed','欧系','未知','林斯比得汽车公司（Rinspeed）于1977年诞生于瑞士，由Founder Frank M・RINderknecht创立，最初的主要业务为进口美国天窗及残障车辆改装。','R','2023-12-18 04:56:40','2023-12-18 04:56:40');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','252','4f01ada7ab0aef68f6c2235a114defd4.png','RIVIAN','RIVIAN','美系','美国','Rivian是一家成立于2009年的美国汽车制造商和汽车技术公司。该公司开发与可持续交通有关的车辆，产品和服务。','R','2023-12-18 04:56:42','2023-12-18 04:56:42');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','330','e146ab6617770ec725a6d7fcd8795200.png','RENOVO','RENOVO','美系','美国','Renovo Motors是美国硅谷的初创新能源汽车公司，他们最初通过打造纯电动复古跑车进入行业，后来转型成为汽车科技公司，为自动驾驶汽车提供ADAS技术开发平台。','R','2023-12-18 04:56:42','2023-12-18 04:56:42');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','358','c5c7ef2825a1b58571415bc39ac65eb7.png','Radical','Radical','英系','英国','Radical Sportscars是英国的赛车制造商和制造商。','R','2023-12-18 04:56:43','2023-12-18 04:56:43');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','524','295c63a3b9da04a4823a8106568fc46a.png','Radford','Radford','英系','英国','Radford是由前F1世界冠军Jenson Button、汽车设计师Mark Stubbs、汽车节目制作与主持人Ant Anstead及律师Roger Behle共同出资收购重组的老牌英国改装品牌。','R','2023-12-18 04:56:43','2023-12-18 04:56:43');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','569','43d1bf808d02fff694577af6d3631874.png','瑞腾汽车','','','','','R','2023-12-18 04:56:43','2023-12-18 04:56:43');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','23','32f16e34c3361eda55eb3842ed7f0f4a.png','斯柯达','Skoda','其它','捷克','斯柯达（捷克语：Skoda Auto）是一家总部位于捷克共和国的汽车制造商。创立于1895年。1991年后斯柯达公司逐渐由国家公有化走向财团私有化，并在2000年成为德国大众集团（VW）旗下的品牌。','S','2023-12-18 04:56:43','2023-12-18 04:56:43');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','26','f7af44f698f637ac59c10e4ebb884260.png','三菱','Mitsubishi','日系','日本','三菱集团（Mitsubishi Group）是由原先日本三菱财阀解体后的公司共同组成的一个松散的实体，Mitsubishi这个名字中的mitsu表示“三”，而bishi表示“菱角”。','S','2023-12-18 04:56:46','2023-12-18 04:56:46');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','108','9ceec705ba89f9665223a09314b8d87e.png','上汽大通MAXUS','MAXUS','自主','中国','上汽大通MAXUS作为上汽旗下的汽车品牌，产品包括MPV、SUV、房车，宽体轻客、皮卡等乘商并举的产品组合。2009年，上汽集团收购LDV公司及旗下MAXUS品牌，在保持其完整的产品技术、纯正的英伦产品血统的同时，结合全球汽车市场洞察，通过战略再定位，并在2011年成立了上汽大通全资子公司，明确了它在上汽集团中创新者的角色，而第一步的创新模式最终确定为C2B智能定制模式。','S','2023-12-18 04:57:06','2023-12-18 04:57:06');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','48','181850897d2126f83374a63737c0e0c4.png','smart','smart','德系','德国','Smart（Smart Automobile）汽车是由吉利控股和德国戴姆勒合资公司经营的迷你车和微型车系列品牌，自90年代品牌诞生以来，smart始终肩负着“探索未来都市交通最佳解决方案”的愿景。2019年，smart品牌全球公司正式成立，以“中欧双核，全球布局”为前瞻发展战略，致力于将smart塑造为全球领先的新奢智能纯电汽车品牌。','S','2023-12-18 04:57:08','2023-12-18 04:57:08');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','515','3d514c6a8e92b0d603268d3d1bb8b22b.png','深蓝汽车','Deepal','自主','中国','深蓝汽车，成立于2022年4月13日，是兼有整车、研发、生产、加工、销售等业务的全产业链新能源汽车品牌，也是长安汽车香格里拉计划和北斗天枢计划的坚定探索者。','S','2023-12-18 04:57:09','2023-12-18 04:57:09');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','235','a1b584a1e7d3d5c956dcc41f1c143ac2.png','思皓','SOL','自主','中国','2018年4月24日，由安徽江淮汽车集团股份有限公司与大众汽车集团（中国）合资成立的江淮大众汽车有限公司（以下简称“江淮大众”）发布全新品牌――SOL（思皓），并亮相该品牌的首款车型E20X。','S','2023-12-18 04:57:09','2023-12-18 04:57:09');
