INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','454','a73d367d16ad57c4e1a94c2aa33aac15.png','申龙客车','sunlongbus','自主','中国','上海申龙客车有限公司位于中国的经济中心――上海，年产能超过1万辆，致力于客车整车研发、制造和销售，产品涵盖新能源客车、公路客运、旅游、公交、团体、厢式物流车等各个细分市场，是国内为数不多的综合性客车制造企业。','S','2023-12-18 04:57:41','2023-12-18 04:57:41');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','275','664f76f2e98a10b749106ae8fbbbb3fa.png','Scion','Scion','日系','日本','赛扬汽车（英语：Scion）是丰田汽车在北美洲推出的第三个汽车品牌，长期目标是吸引Y世代的年轻消费者。丰田在2016年8月的2017款年之初取消了Scion品牌；这些车辆被更名为丰田汽车或停产。','S','2023-12-18 04:57:41','2023-12-18 04:57:41');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','370','9d7847078a27b085f9ba2c7c5a9694dc.png','SONY','SONY','日系','日本','索尼（日语：ソニ`株式会社，英语：Sony Corporation；曾译作“新力”）是源自日本的跨国综合企业，以研制电子产品为主要事业，经营领域横跨消费电子产品、专业性电子产品、电子游戏、金融、娱乐，唱片，电影，半导体等。拥有全世界的品牌知名度。','S','2023-12-18 04:57:42','2023-12-18 04:57:42');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','338','715db245b1c9d9d4e3314fb311716e55.png','Sono Motors','Sono Motors','德系','德国','德国初创公司Sono Motors，正在开发和制造适合日常使用的前瞻性电动汽车，具有集成的太阳能电池和创新的出行服务。','S','2023-12-18 04:57:42','2023-12-18 04:57:42');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('2','340','2c57ab1561f5424027e87ec594d4dff7.png','首望','shou wang','自主','中国','依托北京现代强大的研发、采购、生产和营销实力，以及广大消费者对北京现代品牌与产品的认可，2011年广州车展，北京现代发布了旗下自主品牌――首望。','S','2023-12-18 04:57:42','2023-12-18 04:57:42');
INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','359','220cdbae4add4cfd98b9a5926d8b251d.png','Share2Drive','Share2Drive','德系','德国','Share2Drive是自动驾驶汽车的制造商。它的功能包括推拉门，平坦的地板，智能连接功能和最佳的停车密度。它可用于城市共享出行，例如车队和汽车共享。','S','2023-12-18 04:57:43','2023-12-18 04:57:43');
