INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('2','311','165c7f7e46c7699bf0bc8c3397c656ed.png','上海','shanghai','自主','中国','上汽集团总部设于上海，是中国四大汽车制造商之一。2004年11月29日，上海汽车工业（集团）总公司（隶属上海市国有资产管理委员会）将其与汽车产业链相关的资产和业务剥离，发起成立了上海汽车集团股份有限公司（SAIC Motor Corporation Limited），简称上汽集团股份。','S','2023-12-18 04:57:46','2023-12-18 04:57:46');
