INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','63','1333b3e802e5c9fbc82c7301bb9c216a.png','特斯拉','Tesla','美系','美国','特斯拉是一家美国电动车及能源公司，产销电动车、太阳能板、及储能。总部位于美国加利福尼亚州硅谷的帕罗奥多（Palo Alto），2003年最早由马丁・艾伯哈德（Martin Eberhard）和马克・塔彭宁（Marc Tarpenning）共同创立，2004年埃隆・马斯克（Elon Musk）进入公司并领导了A轮融资。','T','2023-12-18 04:57:46','2023-12-18 04:57:46');
