INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','361','8a7a83a9d19f841476d571d54fc56ffc.png','TOROIDION','TOROIDION','欧系','未知','来自芬兰的Toroidion成立于2011年，旨在开发一种全新的电动动力总成，该动力总成可以竞争并赢得勒芒24小时耐力赛。创始人Pasi Pennanen在过去20年的汽车行业中一直在开发概念车和生产生产模型。','T','2023-12-18 04:57:54','2023-12-18 04:57:54');
