INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','450','61609b0d801a42a3d6c12819d9bf7db8.png','TopCar','TopCar','欧系','俄罗斯','TopCar Design是一家从事豪华车改装零部件研发和生产的国际化公司，拥有自己独特的设计。该公司成立于 2004 年，是世界领先的五家调音工作室之一，目前在 20 多个国家设有办事处。','T','2023-12-18 04:57:55','2023-12-18 04:57:55');
