INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','348','375a6390aab462994cf5111cc0d47b91.png','Uniti','Uniti','其它','瑞典','Uniti是瑞典汽车初创公司，由刘易斯・霍恩创办并担任CEO，Uniti最初是隆德大学的开放式创新项目，到2016年1月发展成为一家独立的创业公司。该公司旨在通过重新设计整辆汽车来改变汽车代码，着重于提供轻量级的数字化的首次用车体验，以及提供完全对地球环境气候零影响的汽车。','U','2023-12-18 04:57:58','2023-12-18 04:57:58');
