INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','164','97eec3cd5d8d49a4f8928f15f6fd7864.png','新凯','XINKAI','自主','中国','新凯集团创建于1984年，历经三十多年的发展，建有高碑店年产10万辆奔驰高档商务车生产基地、涿州年产1万辆专用车生产基地、山东泰安年产15万辆生产基地、高碑店年产20万台发动机生产基地、高碑店市凯龙房地产开发有限公司，涉足汽车整车、专用车、零部件加工、房地产开发等领域。','X','2023-12-18 04:58:54','2023-12-18 04:58:54');
