INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','193','e1e8032375bc864e98afe246b96d2e9a.png','SRM鑫源','SHINERAY','自主','中国','华晨鑫源是由华晨汽车集团与东方鑫源集团有限公司合资组建的大型汽车企业，拥有独立的汽车生产资质。总部位于重庆，在北京、上海、深圳、意大利、美国均有市场布局，公司成立于2012年，拥有完善的设计、研发、制造、销售、服务产业链体系。公司相继获得了国家高新技术企业、重庆市企业技术中心、重庆市知识产权优势企业、重庆市高成长性科技型企业、重庆市工业互联网试点示范企业的称号。','X','2023-12-18 04:58:57','2023-12-18 04:58:57');
