INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','306','5af9e2a0fda447264f794532cf01a0ac.png','谢尔比','SHELBY','美系','美国','谢尔比由传奇人物卡罗尔・谢尔比（Carroll Hall Shelby）创立，通过全球的经销商和合作伙伴制造和销售高性能汽车及相关产品。该公司总部位于内华达州拉斯维加斯，制造真正的延续眼镜蛇，包括427 S / C，289 FIA和289街车零部件。','X','2023-12-18 04:59:00','2023-12-18 04:59:00');
