INSERT INTO `car_brands`(`business_status`,`brand_id`,`image_url`,`brand_name`,`brand_english_name`,`brand_type`,`brand_country`,`content_abstract`,`pinyin`,`created_at`,`updated_at`) VALUES ('1','29','a9ddd8aa71bec1ff4d060fe9339b6568.png','英菲尼迪','infiniti','日系','日本','INFINITI英菲尼迪是豪华车品牌，1989年该品牌诞生于北美地区。总部在日本横滨，是日本品牌，属于日系车，凭借独特前卫的设计、出色的产品性能和客户体验至上的服务，英菲尼迪迅速成为全球豪华汽车市场中最重要的品牌之一。2007年英菲尼迪正式登陆中国。','Y','2023-12-18 04:59:03','2023-12-18 04:59:03');
